import React, { useState, useEffect } from "react";
import Header from "./Components/Header/Header";
import Compare from "./Components/Compare/Compare";
import Recommendation from "./Components/Recommendation/Recommendation";
import axios from "axios";

function App() {
  const [lunar, setLunar] = useState("");

  useEffect(() => {
    axios
      .get(
        "https://api.lunarcrush.com/v2?data=market&key=t7583msb2asugygjl8vcto"
      )
      .then((res) => {
        const data = res.data.data;
        const dataArr = [];
        const btcArr = data.filter((obj) => obj.id === 1);
        const ethArr = data.filter((obj) => obj.id === 2);

        dataArr.push(btcArr[0], ethArr[0]);
        setLunar(dataArr);
        console.log(dataArr);
      })

      .catch((err) => {
        console.error(err);
      }, []);

    axios
      .get("https://data.messari.io/api/v1/assets/btc/metrics")
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

  return (
    <div className="App">
      <span>{lunar && !lunar}</span>
      <Header />
      <Compare name={lunar[i].n} />
      <Recommendation />
    </div>
  );
}

export default App;
